function simpleResponsiveTable(e){for(var t=document.getElementsByClassName(e),a=0;a<t.length;a++)for(var n=t[a],l=n.getElementsByTagName("th"),g=t[a].getElementsByTagName("tr"),m=1;m<g.length;m++)for(var s=g[m].getElementsByTagName("td"),r=0;r<s.length;r++)s[r].setAttribute("data-col-title",l[r].innerHTML)}